import React from "react";

export default class UserWidgetComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            users: false
        }
    }

    componentDidMount() {
        fetch('http://localhost:5000/user')
            .then(res => res.json())
            .then(
                (result) => {
                    result.map(item => this.setState({
                        users: result
                    }));
                }
            )
    }

    render() {
        if (!this.state.users) {
            return <span>Loading Users...</span>
        } else {
            return(
                <span>
                    { this.state.users.map() }
                </span>
            );
        }
    }
}
