import React from "react";
import AddUserFormComponent from "../AddUserForm/AddUserForm.component";

export default class UserWidgetComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            users: false
        }
    }

    componentDidMount() {
        fetch('http://localhost:5000/user')
            .then(res => res.json())
            .then(
                (result) => {
                    result.map(item => this.setState({
                        users: result
                    }));
                }
            )
    }

    render() {
        if (!this.state.users) {
            return <span>Loading Users...<AddUserFormComponent /></span>
        } else {
            let userElements = [];

            this.state.users.map(user => userElements.push(<li key={ user._id }>{ user.username }</li>))

            return (
                <span>
                    <h4>User List:</h4>
                    <ol>
                        { userElements }
                    </ol>
                    <AddUserFormComponent />
                </span>
            );
        }
    }
}
