import React from "react";

export default class EditUserFormComponent extends React.Component {
    constructor(props) {
        super(props);
        this.user = this.props.user;
        this.state = {
            formClass: 'Form-Edit-Hidden',
            username: this.user.username,
            password: this.user.password
        }

        this.handleClick = this.handleClick.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleClick() {
        if (this.state.formClass === 'Form-Edit-Hidden') {
            this.setState({
                formClass: 'Form-Edit-Shown'
            })
        } else {
            this.setState({
                formClass: 'Form-Edit-Hidden'
            })
        }
    }

    handleChange(event) {
        const {name, value} = event.target;

        this.setState({
            [name]: value
        })
    }

    render() {
        return (
            <span>
                <button type='button' onClick={this.handleClick}>Edit</button>
                <form className={this.state.formClass}>
                    <fieldset>
                        <label>
                            Username:
                            <input type='text' name='username' value={this.state.username}
                                   onChange={this.handleChange}/>
                        </label>
                        <label>
                            Password:
                            <input type='text' name='password' value={this.state.password}
                                   onChange={this.handleChange}/>
                        </label>
                        <input type='submit'/>
                    </fieldset>
                </form>
            </span>
        )
    }
}
